import { useEffect, useMemo, useRef, useState } from "react";
import "./Profile.css";

export default function Profile() {
  const initial = useMemo(
    () => ({
      username: "Username",
      email: "username@gmail.com",
      phone: "+63",
      avatarUrl: "",
      preferences: ["Vegetarian", "Meat", "Fastfood"],
    }),
    []
  );

  const [form, setForm] = useState(initial);
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [avatarPreview, setAvatarPreview] = useState(initial.avatarUrl);
  const [isDirty, setIsDirty] = useState(false);
  const prefInputRef = useRef(null);
  const fileRef = useRef(null);

  // ---- Saved dishes (bookmarks from Explorer) ----
  const [favorites, setFavorites] = useState(() => loadFavs());
  useEffect(() => {
    const sync = () => setFavorites(loadFavs());
    window.addEventListener("pap:favorites-updated", sync);
    window.addEventListener("focus", sync);
    return () => {
      window.removeEventListener("pap:favorites-updated", sync);
      window.removeEventListener("focus", sync);
    };
  }, []);

  // detect form changes
  useEffect(() => {
    const changed =
      form.username !== initial.username ||
      form.email !== initial.email ||
      form.phone !== initial.phone ||
      avatarPreview !== initial.avatarUrl ||
      password.length > 0 ||
      JSON.stringify(form.preferences) !==
        JSON.stringify(initial.preferences);
    setIsDirty(changed);
  }, [form, password, avatarPreview, initial]);

  const onChange =
    (field) =>
    (e) =>
      setForm((s) => ({ ...s, [field]: e.target.value }));

  const onPickAvatar = () => fileRef.current?.click();
  const onFile = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const r = new FileReader();
    r.onload = () => setAvatarPreview(r.result);
    r.readAsDataURL(f);
  };

  const removePref = (p) =>
    setForm((s) => ({
      ...s,
      preferences: s.preferences.filter((x) => x !== p),
    }));

  const addPref = () => {
    const val = prefInputRef.current?.value?.trim();
    if (!val) return;
    setForm((s) =>
      s.preferences.includes(val)
        ? s
        : { ...s, preferences: [...s.preferences, val] }
    );
    prefInputRef.current.value = "";
    prefInputRef.current.focus();
  };
  const onPrefKey = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addPref();
    }
  };

  const onSubmit = (e) => {
    e.preventDefault();
    // TODO: Replace with your API call to persist profile + password + avatar
    alert("Saved! Wire this to your API.");
  };
  const logout = () => {
    // TODO: Replace with your auth signout
    alert("Logging out…");
  };

  return (
    <main className="pp-main">
      {/* Banner */}
      <section className="pp-banner" role="img" aria-label="Welcome banner">
        <div>
          <h1 className="pp-banner-title">Hello, {form.username}!</h1>
          <p className="pp-banner-sub">
            Manage your account and personalize your experience.
          </p>
        </div>
      </section>

      {/* Content Grid */}
      <form className="pp-grid" onSubmit={onSubmit}>
        {/* Profile Card */}
        <section className="pp-card pp-profile-card">
          <div className="pp-avatar-wrap">
            <div className="pp-avatar" aria-label="Profile picture">
              {avatarPreview ? (
                <img src={avatarPreview} alt="Avatar" />
              ) : (
                icon("avatar")
              )}
            </div>
            <button
              type="button"
              className="pp-avatar-edit"
              onClick={onPickAvatar}
              aria-label="Change profile picture"
            >
              {icon("edit")}
            </button>
            <input
              ref={fileRef}
              onChange={onFile}
              type="file"
              accept="image/*"
              hidden
              aria-hidden="true"
            />
          </div>

          <ul className="pp-list">
            <li>
              {icon("userLine")}
              <span>{form.username}</span>
            </li>
            <li>
              {icon("mail")}
              <span>{form.email}</span>
            </li>
            <li>
              {icon("phoneLine")}
              <span>{form.phone}</span>
            </li>
          </ul>

          <div className="pp-actions-vertical">
            <button
              className="pp-btn pp-btn-primary"
              type="submit"
              disabled={!isDirty}
            >
              Save Changes
            </button>
            <button
              className="pp-btn pp-btn-ghost"
              type="button"
              onClick={logout}
            >
              Logout
            </button>
          </div>
        </section>

        {/* Details + Password */}
        <section className="pp-card">
          <div className="pp-field">
            <label htmlFor="username">Username</label>
            <div className="pp-input-wrap">
              {icon("userLine")}
              <input
                id="username"
                value={form.username}
                onChange={onChange("username")}
                placeholder="Your username"
                autoComplete="username"
              />
            </div>
          </div>

          <div className="pp-field">
            <label htmlFor="email">Email</label>
            <div className="pp-input-wrap">
              {icon("mail")}
              <input
                id="email"
                type="email"
                value={form.email}
                onChange={onChange("email")}
                placeholder="you@example.com"
                autoComplete="email"
              />
            </div>
          </div>

          <div className="pp-field">
            <label htmlFor="phone">Phone Number</label>
            <div className="pp-input-wrap">
              {icon("phoneLine")}
              <input
                id="phone"
                value={form.phone}
                onChange={onChange("phone")}
                placeholder="+63"
                inputMode="tel"
                autoComplete="tel"
              />
            </div>
          </div>

          <div className="pp-field">
            <label htmlFor="password">Password</label>
            <div className="pp-input-wrap">
              {icon("lock")}
              <input
                id="password"
                type={showPassword ? "text" : "password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="••••••••"
                autoComplete="new-password"
              />
              <button
                type="button"
                className="pp-eye"
                onClick={() => setShowPassword((s) => !s)}
                aria-label={showPassword ? "Hide password" : "Show password"}
              >
                {icon(showPassword ? "eyeOff" : "eye")}
              </button>
            </div>
            <small className="pp-help">
              Leave blank to keep your current password.
            </small>
          </div>
        </section>

        {/* Preferences */}
        <section className="pp-card">
          <h2 className="pp-section-title">Food Preferences</h2>
          {form.preferences.length === 0 ? (
            <p className="pp-muted">
              No preferences yet. Add some below to personalize your
              recommendations.
            </p>
          ) : (
            <div className="pp-chip-list" role="list">
              {form.preferences.map((p) => (
                <button
                  key={p}
                  type="button"
                  className="pp-chip"
                  onClick={() => removePref(p)}
                  aria-label={`Remove ${p}`}
                  title="Click to remove"
                >
                  <span>{p}</span>
                  {icon("x")}
                </button>
              ))}
            </div>
          )}
          <div className="pp-addpref">
            <div className="pp-input-wrap">
              {icon("plus")}
              <input
                ref={prefInputRef}
                onKeyDown={onPrefKey}
                placeholder="Add preference (e.g., Spicy, Halal, Street Food)"
                aria-label="Add preference"
              />
            </div>
            <button
              type="button"
              className="pp-btn pp-btn-primary"
              onClick={addPref}
            >
              Add
            </button>
          </div>
        </section>

        {/* Saved Dishes (from Explorer bookmarks) */}
        <section className="pp-card">
          <h2 className="pp-section-title">Saved Dishes</h2>
          {favorites.length === 0 ? (
            <p className="pp-muted">
              No bookmarks yet. Save dishes in the Cultural Food Explorer.
            </p>
          ) : (
            <div className="pp-favs">
              {favorites.map((d) => (
                <div key={d.id} className="pp-fav">
                  <img
                    src={d.img}
                    alt={d.name}
                    width={56}
                    height={56}
                    loading="lazy"
                  />
                  <div className="pp-fav-meta">
                    <strong className="pp-fav-name">{d.name}</strong>
                    <small className="pp-fav-region">{d.region}</small>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>

        {/* Sticky Save for mobile */}
        <div className="pp-savebar">
          <button
            className="pp-btn pp-btn-primary"
            type="submit"
            disabled={!isDirty}
          >
            Save Changes
          </button>
        </div>
      </form>
    </main>
  );
}

/* helpers */
function loadFavs() {
  try {
    const raw = localStorage.getItem("pap_favorites");
    return raw ? JSON.parse(raw) : [];
  } catch {
    return [];
  }
}

/* same inline icons as before */
function icon(name) {
  const common = { width: 20, height: 20, "aria-hidden": true, focusable: false };
  switch (name) {
    case "avatar":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M12 12a5 5 0 1 0-5-5 5 5 0 0 0 5 5zm0 2c-5 0-9 2.5-9 5.5V22h18v-2.5C21 16.5 17 14 12 14z"/>
        </svg>
      );
    case "edit":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M3 17.3V21h3.7l10.8-10.8-3.7-3.7L3 17.3zM20.7 7a1 1 0 000-1.4L18.4 3.3a1 1 0 00-1.4 0l-1.8 1.8 3.7 3.7 1.8-1.8z"/>
        </svg>
      );
    case "userLine":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M12 12a4.5 4.5 0 1 0-4.5-4.5A4.5 4.5 0 0 0 12 12Zm0 1.5c-4.7 0-8.5 2.3-8.5 5.2V21h17v-2.3c0-2.9-3.8-5.2-8.5-5.2z"/>
        </svg>
      );
    case "mail":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M20 4H4a2 2 0 00-2 2v1l10 6 10-6V6a2 2 0 00-2-2zm0 6.2l-8 4.8-8-4.8V18a2 2 0 002 2h12a2 2 0 002-2V10.2z"/>
        </svg>
      );
    case "phoneLine":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M6.7 10.7a14.8 14.8 0 0 0 6.6 6.6l2.2-2.2a1 1 0 0 1 1.1-.3 12 12 0 0 0 4 .6 1 1 0 0 1 1 1V21a1 1 0 0 1-1 1C10.2 22 2 13.8 2 3a1 1 0 0 1 1-1h3.5a1 1 0 0 1 1 1c0 1.4.2 2.8.6 4a1 1 0 0 1-.3 1.1l-2.1 2.6z"/>
        </svg>
      );
    case "lock":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M17 9h-1V7a4 4 0 1 0-8 0v2H7a2 2 0 0 0-2 2v8a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2v-8a2 2 0 0 0-2-2zm-7-2a2 2 0 1 1 4 0v2h-4V7z"/>
        </svg>
      );
    case "eye":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M12 5C7 5 2.7 8.1 1 12c1.7 3.9 6 7 11 7s9.3-3.1 11-7c-1.7-3.9-6-7-11-7zm0 12a5 5 0 1 1 5-5 5 5 0 0 1-5 5z"/>
        </svg>
      );
    case "eyeOff":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M2 5.3L3.3 4 20 20.7 18.7 22l-2.4-2.4A12.3 12.3 0 0 1 12 19C7 19 2.7 15.9 1 12a13.4 13.4 0 0 1 2.9-3.9L2 5.3zM12 7a5 5 0 0 1 4.6 7l-1.6-1.6A2.9 2.9 0 0 0 12 9a3 3 0 0 0-3 3c0 .6.2 1.2.4 1.7L7.4 12A5 5 0 0 1 12 7z"/>
        </svg>
      );
    case "x":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M18 6L6 18M6 6l12 12" strokeWidth="2" stroke="currentColor" fill="none" />
        </svg>
      );
    case "plus":
      return (
        <svg {...common} viewBox="0 0 24 24">
          <path d="M11 5h2v6h6v2h-6v6h-2v-6H5v-2h6z"/>
        </svg>
      );
    default:
      return null;
  }
}
