import { useState } from "react";
import "./Explorer.css";

const dishes = [
  {
    id: 1,
    name: "Sinigang na Baboy",
    desc: "Sinigang, a sour Filipino stew usually flavored with tamarind.",
    region: "Luzon",
    img: "/images/sinigang.jpg",
    recipe: [
      "1 kg pork belly or ribs",
      "8 cups water",
      "2 tomatoes, quartered",
      "1 onion, sliced",
      "1 radish, sliced",
      "Kangkong leaves",
      "Tamarind soup base (or fresh tamarind)",
      "Fish sauce, salt, pepper to taste",
    ],
  },
  {
    id: 2,
    name: "Adobong Manok",
    desc: "Adobo is chicken simmered in vinegar, soy sauce, garlic, and peppercorns.",
    region: "Luzon",
    img: "/images/adobo.jpg",
    recipe: [
      "1 kg chicken, cut into pieces",
      "1/2 cup soy sauce",
      "1/2 cup vinegar",
      "6 cloves garlic",
      "3 bay leaves",
      "Peppercorns",
    ],
  },
  {
    id: 3,
    name: "Kare-Kare",
    desc: "A peanut-based stew with oxtail and vegetables.",
    region: "Luzon",
    img: "/images/karakare.jpg",
    recipe: [
      "1 kg oxtail (or pork hocks)",
      "1/2 cup peanut butter",
      "1 banana heart, sliced",
      "String beans",
      "Eggplant",
      "Bagoong (shrimp paste) for serving",
    ],
  },
];

export default function Explorer() {
  const [region, setRegion] = useState("All");
  const [expandedId, setExpandedId] = useState(null);

  const toggleRecipe = (id) => {
    setExpandedId((prev) => (prev === id ? null : id));
  };

  const filtered =
    region === "All" ? dishes : dishes.filter((d) => d.region === region);

  return (
    <div className="explorer">
      <div className="explorer-header">
        <h1>Explore Filipino Cuisine</h1>
        <select value={region} onChange={(e) => setRegion(e.target.value)}>
          <option value="All">All Regions</option>
          <option value="Luzon">Luzon</option>
          <option value="Visayas">Visayas</option>
          <option value="Mindanao">Mindanao</option>
        </select>
      </div>

      <div className="explorer-grid">
        {filtered.map((dish) => {
          const isOpen = expandedId === dish.id;
          return (
            <div key={dish.id} className="dish-card">
              <img src={dish.img} alt={dish.name} />
              <h2>{dish.name}</h2>
              <p>{dish.desc}</p>

              <button onClick={() => toggleRecipe(dish.id)}>
                {isOpen ? "Hide Recipe" : "Show Recipe"}
              </button>

              {isOpen && (
                <div className="recipe-box">
                  <h3>Recipe:</h3>
                  <ul>
                    {dish.recipe.map((step, idx) => (
                      <li key={idx}>{step}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}
